general.customPopupCss:
.gloss-content { font-family: "UD Digi Kyokasho NK-R" !important; }
.source-text { font-family: "UD Digi Kyokasho NK-R" !important; }

/* width */
::-webkit-scrollbar {
  width: 6px !important;
}

/* Track */
::-webkit-scrollbar-track {
  box-shadow: inset 0 0 3px grey !important;
  border-radius: 5px !important;
}

/* Handle */
::-webkit-scrollbar-thumb {
  background: #989898 !important;; 
  border-radius: 10px;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #b0b0b0 !important;
}

